import 'bootstrap/dist/css/bootstrap.css';
import React, { Component } from 'react'
import Todo from './Components/Todo'
import Modal from './Components/Modal';

const data = [{
  "id":1,
  "title":"Finish eccommerce",
  "description":"Finish react app eccommerce",
  "completed":false
},
  {
    "id":2,
    "title":"Trash",
    "description":"Take the trash out before 8pm",
    "completed":true
}]

export default class App extends Component {
  state = {
    showModal: false,
  }
  handleAddTaskClick = () =>{
    this.setState({
      showModal: !this.state.showModal, 
    })
  }
  render() {
    return (
      <main class="container">
        <Modal show={true} />
        <h1 class="text-center text-uppercase
                  my-4">Todo App</h1>

        <div class="row">
          <div class="col mx-auto">
            <div class="card p-3">
              <div class="mb-4">
                <button class="btn btn-primary" onClick={this.handleAddTaskClick}>
                  Add Task
                </button>
              </div>
              <ul className='list-group'>
                {data.map((todoData, i) => (
                  <Todo title={todoData.title} completed={todoData.completed}/>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </main>
    )
  }
}
