import axios from 'axios';

export function fetchTodos() {
    return axios.get("/api/todos/")
    .catch(err =>{
        console.error(err)
    })
}

export function newTodo(item) {
    axios.post(`/api/todos/${item.id}/`, item)
    .then(res => res.status);
}
