import 'bootstrap/dist/css/bootstrap.css';
import React, { Component } from 'react'
import Todo from './Components/Todo'
import Button from 'react-bootstrap/Button'
import Modal from './Components/Modal';
import {fetchTodos} from "./utils.js"

export default class App extends Component {
  state = {
    show: false,
    handleShow: () => this.setState({show:true}),
    handleClose: () => this.setState({show:false}),
    todos: []
  }

  refreshTodos(){
    fetchTodos().then(data => this.setState({todos: data.results}))

  }

  componentDidMount = () =>{
    this.refreshTodos()
    console.log(this.state.todos)
  }



  render() {
    return (
      <main className="container">
        <h1 className="text-center text-uppercase
                  my-4">Todo App</h1>

        <div className="row">
          <div className="col mx-auto">
            <div className="card p-3">
              <div className="mb-4">
              <Button variant="primary" onClick={this.state.handleShow}>
                Launch demo modal
              </Button>
              </div>
              <ul className='list-group'>
                {/* {this.state.todos.map((todoData, i) => (
                  <Todo key={i} title={todoData.title} completed={todoData.completed}/>
                ))} */}
              </ul>
            </div>
          </div>
        </div>
        <Modal show={this.state.show} 
        closeFunction={this.state.handleClose}/>
      </main>
    )
  }
}
