import React, {useState, useEffect, Component} from 'react'
import {Modal as BsModal, Button, Form} from 'react-bootstrap'

class Modal extends Component {
    constructor(props){
      super(props);
      this.state = {
        current: this.props.item,
      }
    }

    handleChange = (e) => {

      let name = e.target.name;
      let value = (e.target.type === 'checkbox') ? e.target.checked :  e.target.value;

      const newItem = {...this.state.current, [name]: value}
      this.setState({current: newItem})
    } 

    render(){
      const {toggle, onSubmit} = this.props;
    return (
        <BsModal show={toggle}>
      <BsModal.Header closeButton>
          <BsModal.Title>New Task</BsModal.Title>
        </BsModal.Header>
        <BsModal.Body>
            {/* New Task Form */}
            <Form >
              <Form.Group>
                  <Form.Control 
                  value={this.state.current.title} 
                  type="text" placeholder="Title" 
                  name='title'
                  onChange={this.handleChange}
                  />
              </Form.Group>
              <Form.Group className="mt-3">
                  <Form.Check type="checkbox" label="Completed"
                    name='completed'
                    checked={this.state.current.completed}
                    onChange={this.handleChange}
                  />
              </Form.Group>
            </Form>
        </BsModal.Body>
        <BsModal.Footer>
          <Button variant="secondary" onClick={toggle}>
            Close
          </Button>
          <Button 
          variant="primary"
          onClick={() => onSubmit(this.state.current)}>
            Save
          </Button>
        </BsModal.Footer>
      </BsModal>
    )}
}

export default Modal
