import React, {useState, useEffect, Component} from 'react'
import {Modal as BsModal, Button, Form} from 'react-bootstrap'

class Modal extends Component {
    constructor(props, {show, toggle, item, onSubmit, }){
      super(props);
      this.state = {
        current: item,
      }
    }

    handleChange = (e) => {
      let name = e.target.name;
      let value = (e.target.type === 'checkbox') ? e.target.checked :  e.target.value;

      const newItem = {...current, [name]: value}
      setCurrent(newItem)
    } 

    render(){
    return (
        <BsModal show={show}>
        <BsModal.Header closeButton>
          <BsModal.Title>New Task</BsModal.Title>
        </BsModal.Header>
        <BsModal.Body>
            {/* New Task Form */}
            <Form >
              <Form.Group>
                  <Form.Control 
                  value={current.title} 
                  type="text" placeholder="Title" 
                  name='title'
                  onChange={handleChange}
                  />
              </Form.Group>
              <Form.Group className="mt-3">
                  <Form.Check type="checkbox" label="Completed"
                    name='completed'
                    checked={current.completed}
                    onChange={handleChange}
                  />
              </Form.Group>
            </Form>
        </BsModal.Body>
        <BsModal.Footer>
          <Button variant="secondary" onClick={toggle}>
            Close
          </Button>
          <Button 
          variant="primary"
          onClick={() => onSubmit(current)}>
            Save
          </Button>
        </BsModal.Footer>
      </BsModal>
    )}
}

export default Modal
