import axios from 'axios';

export function fetchTodos() {
    return axios.get("/api/todos/")
    .catch(err =>{
        console.error(err)
    })
}

export function newTodo(item) {
    try{
        axios.post("http://localhost:8000/api/todos/", item)
    .then(res => res.status)
    }catch(e){
        console.error(e)
    }

}

export function saveTodo(item) {
    var url = "http://localhost:8000/api/todos/";
    if(item.id) url = `http://localhost:8000/api/todos/${item.id}/`
    try{
        if(item.id) {
            return axios.put(`http://localhost:8000/api/todos/${item.id}/` ,item)
        }
        return axios.post("http://localhost:8000/api/todos/", item)
    }catch(e){
        console.error(e)
    }
}

export function deleteTodo(item) {
    try{
        axios.delete(`http://localhost:8000/api/todos/${item.id}/`)
    .then(res => res.status)
    }catch(e){
        console.error(e)
    }

}
