import axios from 'axios';

export function fetchTodos() {
    return axios.get("/api/todos/")
    .catch(err =>{
        console.error(err)
    })
}

export function newTodo(item) {
    try{
        axios.post("http://localhost:8000/api/todos/", item)
    .then(res => res.status)
    }catch(e){
        console.error(e)
    }

}

export function editTodo(item) {
    var url = "http://localhost:8000/api/todos/";
    if(item.id)
    try{
        axios.put(`http://localhost:8000/api/todos/${item.id}/`, item)
    .then(res => res.status)
    }catch(e){
        console.error(e)
    }
}

export function deleteTodo(item) {
    try{
        axios.delete(`http://localhost:8000/api/todos/${item.id}/`)
    .then(res => res.status)
    }catch(e){
        console.error(e)
    }

}
